--
-- 表的结构 `cg_users`
--

CREATE TABLE `cg_users` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `user_login` varchar(60) NOT NULL DEFAULT '' COMMENT '用户名',
  `user_pass` varchar(64) NOT NULL DEFAULT '' COMMENT '登录密码；sp_password加密',
  `user_nicename` varchar(50) DEFAULT NULL COMMENT '用户昵称',
  `user_email` varchar(100) DEFAULT '' COMMENT '登录邮箱',
  `user_url` varchar(100) DEFAULT '' COMMENT '用户个人网站',
  `avatar` varchar(255) DEFAULT NULL COMMENT '用户头像，相对于upload/avatar目录',
  `sex` smallint(1) DEFAULT '0' COMMENT '性别；0：保密，1：男；2：女',
  `birthday` varchar(15) DEFAULT NULL COMMENT '年龄',
  `signature` varchar(255) DEFAULT NULL COMMENT '个性签名',
  `last_login_ip` varchar(16) DEFAULT NULL COMMENT '最后登录ip',
  `last_login_time` datetime NOT NULL DEFAULT '2000-01-01 00:00:00' COMMENT '最后登录时间',
  `create_time` datetime NOT NULL DEFAULT '2000-01-01 00:00:00' COMMENT '注册时间',
  `user_activation_key` varchar(60) DEFAULT '' COMMENT '激活码',
  `user_status` int(11) DEFAULT '1' COMMENT '用户状态 0：禁用； 1：正常 ；2：未验证',
  `score` int(11) DEFAULT '0' COMMENT '用户积分',
  `user_type` smallint(1) DEFAULT '1' COMMENT '用户类型，1:admin ;2:会员',
  `coin` int(11) DEFAULT NULL COMMENT '金币',
  `tel` varchar(20) DEFAULT NULL COMMENT '手机号',
  `much` int(11) DEFAULT NULL COMMENT '收入',
  `hair` smallint(1) DEFAULT NULL COMMENT '发质',
  `skin` smallint(1) DEFAULT NULL COMMENT '肤质',
  `addr` varchar(300) DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='用户表';

